-- HIERARQUIA VAREJO --
UPDATE CARGO SET FK_CARGO_SUPERIOR = (SELECT ID FROM CARGO WHERE CODIGO = 'VAREJO_GERENTE')  WHERE CODIGO = 'VAREJO_COORDENADOR';
UPDATE CARGO SET FK_CARGO_SUPERIOR = (SELECT ID FROM CARGO WHERE CODIGO = 'VAREJO_COORDENADOR')  WHERE CODIGO = 'VAREJO_SUPERVISOR';
UPDATE CARGO SET FK_CARGO_SUPERIOR = (SELECT ID FROM CARGO WHERE CODIGO = 'VAREJO_SUPERVISOR')  WHERE CODIGO = 'VAREJO_VENDEDOR';
UPDATE CARGO SET FK_CARGO_SUPERIOR = (SELECT ID FROM CARGO WHERE CODIGO = 'VAREJO_SUPERVISOR')  WHERE CODIGO = 'VAREJO_VENDEDOR_BACKOFFICE';
UPDATE CARGO SET FK_CARGO_SUPERIOR = (SELECT ID FROM CARGO WHERE CODIGO = 'VAREJO_SUPERVISOR')  WHERE CODIGO = 'VAREJO_BACKOFFICE';

-- DEPARTAMENTO COMERCIAL VAREJO --




